// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

struct SignatureRSV {
    bytes32 r;
    bytes32 s;
    uint256 v;
}

contract PrivacyERC20EIP712 {
    bytes32 public constant EIP712_DOMAIN_TYPEHASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );

    bytes32 public constant BALANCEOF_CODE = keccak256("Whispr.balanceOf");
    bytes32 public constant APPROVE_CODE = keccak256("Whispr.approve");

    bytes32 public DOMAIN_BALANCEOF;
    bytes32 public DOMAIN_APPROVE;

    struct ApproveData {
        address owner;
        address spender;
        uint256 amount;
        uint256 nonce;
        uint256 validAfter;
        uint256 validUntil;
        SignatureRSV rsv;
    }

    struct BalanceOfData {
        address owner;
        uint256 validAfter;
        uint256 validUntil;
        SignatureRSV rsv;
    }

    mapping(address => uint256) internal _nonce_eip712;

    function __PrivacyERC20EIP712() public {
        DOMAIN_BALANCEOF = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256("Whispr.balanceOf"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );

        DOMAIN_APPROVE = keccak256(
            abi.encode(
                EIP712_DOMAIN_TYPEHASH,
                keccak256("Whispr.approve"),
                keccak256("1"),
                block.chainid,
                address(this)
            )
        );
    }

    modifier authenticatedBalance(BalanceOfData calldata auth) {
        require(
            block.timestamp >= auth.validAfter,
            "PrivacyERC20EIP712: signature not yet valid"
        );
        require(
            block.timestamp <= auth.validUntil,
            "PrivacyERC20EIP712: signature expired"
        );
        bytes32 BALANCEOF_TYPEHASH = keccak256(
            bytes(
                "balanceOf(address owner,uint256 validAfter,uint256 validUntil)"
            )
        );
        // Validate EIP-712 sign-in authentication.
        bytes32 authdataDigest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_BALANCEOF,
                keccak256(
                    abi.encode(
                        BALANCEOF_TYPEHASH,
                        auth.owner,
                        auth.validAfter,
                        auth.validUntil
                    )
                )
            )
        );

        address recovered_address = ecrecover(
            authdataDigest,
            uint8(auth.rsv.v),
            auth.rsv.r,
            auth.rsv.s
        );
        require(
            auth.owner == recovered_address,
            "PrivacyERC20EIP712: Invalid BalanceOf Authentication"
        );
        _;
    }

    modifier authenticatedApprove(ApproveData calldata auth) {
        require(
            block.timestamp >= auth.validAfter,
            "PrivacyERC20EIP712: signature not yet valid"
        );
        require(
            block.timestamp <= auth.validUntil,
            "PrivacyERC20EIP712: signature expired"
        );
        require(
            auth.nonce == _nonce_eip712[auth.owner],
            "PrivacyERC20EIP712: invalid nonce"
        );
        bytes32 APPROVE_TYPEHASH = keccak256(
            "approve(address owner,address spender,uint256 amount,uint256 nonce,uint256 validAfter,uint256 validUntil)"
        );
        // Validate EIP-712 sign-in authentication.
        bytes32 authdataDigest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                DOMAIN_APPROVE,
                keccak256(
                    abi.encode(
                        APPROVE_TYPEHASH,
                        auth.owner,
                        auth.spender,
                        auth.amount,
                        auth.nonce,
                        auth.validAfter,
                        auth.validUntil
                    )
                )
            )
        );

        address recovered_address = ecrecover(
            authdataDigest,
            uint8(auth.rsv.v),
            auth.rsv.r,
            auth.rsv.s
        );

        require(
            auth.owner == recovered_address,
            "PrivacyERC20EIP712: Invalid Appvove Authentication"
        );
        _;
    }

    function _getNonceEIP712(address owner) internal view returns (uint256) {
        return _nonce_eip712[owner];
    }

    function _incrementNonceEIP712(address owner) internal {
        _nonce_eip712[owner]++;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "./PrivacyPolicy.sol";
import "./PrivacyERC20Verify.sol";
import "./PrivacyERC20EIP712.sol";

contract PrivacyERC20Upgradeable is
    IERC20,
    LuminexPrivacyPolicy,
    AccessControlUpgradeable,
    PrivacyERC20Verify,
    PrivacyERC20EIP712
{
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    mapping(address => uint256) internal _balances;
    mapping(address => mapping(address => uint256)) internal _allowances;

    string public name;
    string public symbol;
    uint8 public decimals;

    uint256 internal _globalTotalSupply;

    function initialize(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        address owner
    ) public initializer {
        __PrivacyERC20EIP712();
        __AccessControl_init();
        name = name_;
        symbol = symbol_;
        decimals = decimals_;
        _grantRole(DEFAULT_ADMIN_ROLE, owner);
    }

    function mint(
        address to,
        uint256 amount
    ) external virtual onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function burn(uint256 amount) external virtual {
        _burn(msg.sender, amount);
    }

    function _isAllowedByPrivacyPolicy(
        address owner
    ) private view returns (bool) {
        return
            _msgSender() == owner ||
            msg.sender == address(this) ||
            hasAccess(
                owner,
                _msgSender(),
                LuminexPrivacyPolicy.PrivacyPolicy.Reveal
            );
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _globalTotalSupply;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        if (!_isAllowedByPrivacyPolicy(account)) {
            return 0;
        }

        return _balances[account];
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        if (!_isAllowedByPrivacyPolicy(owner)) {
            return 0;
        }

        if (_allowances[owner][spender] > 0 && _msgSender() == spender) {
            return _allowances[owner][spender];
        }

        return _allowances[owner][spender];
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
    }

    function approve(
        address spender,
        uint256 amount
    ) external virtual returns (bool) {
        address owner = msg.sender;
        _approve(owner, spender, amount);
        return true;
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = _allowances[owner][spender];
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC20: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[from] = fromBalance - amount;
        }

        _balances[to] += amount;
    }

    function transfer(
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address owner = msg.sender;
        _transfer(owner, to, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = msg.sender;
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function _mint(address to, uint256 amount) internal {
        _balances[to] += amount;
        _globalTotalSupply += amount;
    }

    function _burn(address from, uint256 amount) internal {
        _balances[from] -= amount;
        _globalTotalSupply -= amount;
    }

    function getNonce(address account) public view returns (uint256) {
        if (!_isAllowedByPrivacyPolicy(account)) {
            return 0;
        }
        return _getNonce(account);
    }

    function verifyApprove(
        address owner,
        address spender,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) public view {
        _verifyApprove(owner, spender, amount, data, signature);
    }

    function verifyTransfer(
        address owner,
        address spender,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) public view {
        _verifyTransfer(owner, spender, amount, data, signature);
    }

    function approveUseSignature(
        address owner,
        address spender,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) external virtual returns (bool) {
        _verifyApprove(owner, spender, amount, data, signature);
        _incrementNonce(owner);
        _approve(owner, spender, amount);
        return true;
    }

    function transferUseSignature(
        address from,
        address to,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) external virtual returns (bool) {
        _verifyTransfer(from, to, amount, data, signature);
        _incrementNonce(from);
        _transfer(from, to, amount);
        return true;
    }

    function balanceOfByEIP712(
        BalanceOfData calldata auth
    ) external view authenticatedBalance(auth) returns (uint256) {
        return _balances[auth.owner];
    }

    function getNonceEIP712(address account) external view returns (uint256) {
        if (!_isAllowedByPrivacyPolicy(account)) {
            return 0;
        }
        return _getNonceEIP712(account);
    }

    function approveByEIP712(
        ApproveData calldata auth
    ) external authenticatedApprove(auth) returns (bool) {
        _incrementNonceEIP712(auth.owner);
        _approve(auth.owner, auth.spender, auth.amount);
        return true;
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./PrivacyPolicy.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {MessageHashUtils} from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";

contract PrivacyERC20Verify {
    mapping(address => uint256) private _nonce;

    bytes32 public APPROVE_ACTION = keccak256("APPROVE");

    bytes32 public TRANSFER_ACTION = keccak256("TRANSFER");

    function _getNonce(address owner) internal view returns (uint256) {
        return _nonce[owner];
    }

    function _verifyApprove(
        address owner,
        address spender,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) internal view {
        (
            address _owner,
            address _spender,
            uint256 _amount,
            bytes32 _action,
            uint256 _nonceNumber,
            uint256 _validAfter,
            uint256 _validUntil
        ) = abi.decode(
                data,
                (address, address, uint256, bytes32, uint256, uint256, uint256)
            );
        require(owner == _owner, "PrivacyERC20Verify: owner mismatch");
        require(spender == _spender, "PrivacyERC20Verify: spender mismatch");
        require(amount == _amount, "PrivacyERC20Verify: amount mismatch");
        require(
            _action == APPROVE_ACTION,
            "PrivacyERC20Verify: action mismatch"
        );
        require(
            _nonceNumber == _nonce[owner],
            "PrivacyERC20Verify: invalid nonce"
        );
        require(
            block.timestamp >= _validAfter,
            "PrivacyERC20Verify: signature not yet valid"
        );
        require(
            block.timestamp <= _validUntil,
            "PrivacyERC20Verify: signature expired"
        );
        require(
            verifyEthMessage(owner, data, signature),
            "PrivacyERC20Verify: invalid signature"
        );
    }

    function _verifyTransfer(
        address owner,
        address spender,
        uint256 amount,
        bytes calldata data,
        bytes calldata signature
    ) internal view {
        (
            address _owner,
            address _spender,
            uint256 _amount,
            bytes32 _action,
            uint256 _nonceNumber,
            uint256 _validAfter,
            uint256 _validUntil
        ) = abi.decode(
                data,
                (address, address, uint256, bytes32, uint256, uint256, uint256)
            );
        require(owner == _owner, "PrivacyERC20Verify: owner mismatch");
        require(spender == _spender, "PrivacyERC20Verify: spender mismatch");
        require(amount == _amount, "PrivacyERC20Verify: amount mismatch");
        require(
            _action == TRANSFER_ACTION,
            "PrivacyERC20Verify: action mismatch"
        );
        require(
            _nonceNumber == _nonce[owner],
            "PrivacyERC20Verify: invalid nonce"
        );
        require(
            block.timestamp >= _validAfter,
            "PrivacyERC20Verify: signature not yet valid"
        );
        require(
            block.timestamp <= _validUntil,
            "PrivacyERC20Verify: signature expired"
        );

        require(
            verifyEthMessage(owner, data, signature),
            "PrivacyERC20Verify: invalid signature"
        );
    }

    function verifyEthMessage(
        address signer,
        bytes calldata data,
        bytes calldata signature
    ) public pure returns (bool) {
        bytes32 messageHash = keccak256(data);
        bytes32 ethSignedMessageHash = MessageHashUtils.toEthSignedMessageHash(
            messageHash
        );

        address recoveredSigner = ECDSA.recover(
            ethSignedMessageHash,
            signature
        );

        return recoveredSigner == signer;
    }

    function _incrementNonce(address owner) internal {
        _nonce[owner]++;
    }
}

