// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./PrivacyERC20.sol";

contract PrivacyERC20Factory {
    event PrivacyERC20Created(address contractAddress);

    function deploy(
        string memory name,
        string memory symbol,
        uint8 decimals,
        address owner
    ) external returns (address) {
        address thornERC20 = address(
            new PrivacyERC20(name, symbol, decimals, owner)
        );

        emit PrivacyERC20Created(thornERC20);
        return thornERC20;
    }
}

